import React from "react";
import { Toaster } from "react-hot-toast";

import { Header } from "./components/layouts/Header";
import { UploadTabs } from "./components/UploadTabs";
import { TextInput } from "./components/TextInput";
import { FileUpload } from "./components/FileUpload";
import { ModelSelector } from "./components/ModelSelector";
import { Results } from "./components/Results";
import { ErrorMessage } from "./components/ErrorMessage";
import useEmailAnalysis from "./hooks/useEmailAnalysis";

import "./App.css";

const API_BASE_URL = "http://127.0.0.1:5000";

function App() {
  const [activeTab, setActiveTab] = React.useState("text");
  const [emailText, setEmailText] = React.useState("");
  const [uploadedFile, setUploadedFile] = React.useState(null);
  const [analysisModel, setAnalysisModel] = React.useState("gemini");

  const [loading, setLoading] = React.useState(false);
  const [result, setResult] = React.useState(null);
  const [error, setError] = React.useState(null);

  const { analyzeText, analyzeFile } = useEmailAnalysis({
    setLoading,
    setResult,
    setError,
  });

  const clearState = () => {
    setError(null);
    setResult(null);
  };

  const handleTextSubmit = async () => {
    if (!emailText.trim()) {
      setError("Por favor, insira o texto do email");
      return;
    }
    setLoading(true);
    clearState();
    await analyzeText(emailText, analysisModel);
    setLoading(false);
  };

  const handleFileSubmit = async () => {
    if (!uploadedFile) {
      setError("Por favor, selecione um arquivo");
      return;
    }
    setLoading(true);
    clearState();
    await analyzeFile(uploadedFile, analysisModel);
    setLoading(false);
  };

  const handleSubmit = () => {
    if (activeTab === "text") handleTextSubmit();
    else handleFileSubmit();
  };

  const handleClearText = () => {
    setEmailText("");
    clearState();
  };

  return (
    <>
      <Header />
      <Toaster position="top-right" reverseOrder={false} />

      <div className="container">
        <div className="main-card">
          <div className="upload-section">
            <h2 className="section-title">Insira um e-mail para an√°lise</h2>
            <p className="section-description">
              Pode colar um texto ou fazer o upload de um ficheiro.
            </p>

            <UploadTabs
              activeTab={activeTab}
              setActiveTab={setActiveTab}
              clearState={clearState}
              setEmailText={setEmailText}
              setUploadedFile={setUploadedFile}
            />

            {activeTab === "text" ? (
              <TextInput
                emailText={emailText}
                setEmailText={setEmailText}
                handleClearText={handleClearText}
              />
            ) : (
              <FileUpload
                uploadedFile={uploadedFile}
                setUploadedFile={setUploadedFile}
                clearState={clearState}
              />
            )}

            <ModelSelector
              analysisModel={analysisModel}
              setAnalysisModel={setAnalysisModel}
            />

            <button
              className="submit-button"
              onClick={handleSubmit}
              disabled={
                loading ||
                (activeTab === "text" && !emailText.trim()) ||
                (activeTab === "file" && !uploadedFile)
              }
            >
              {loading ? (
                <div className="loading">
                  <div className="spinner"></div>Analisando...
                </div>
              ) : (
                "Analisar e-mail"
              )}
            </button>
          </div>

          {error && <ErrorMessage error={error} />}
          {result && <Results result={result} />}
        </div>
      </div>
    </>
  );
}

export default App;
