import { useState } from "react";
import axios from "axios";

const API_BASE_URL = "http://127.0.0.1:5000";

export default function useEmailAnalysis() {
  const [loading, setLoading] = useState(false);
  const [result, setResult] = useState(null);
  const [error, setError] = useState(null);

  // Para texto
  const analyzeText = async (text, model = "gemini") => {
    if (!text) {
      setError("Por favor, insira o texto do email");
      return;
    }
    setLoading(true);
    setError(null);
    setResult(null);

    try {
      const response = await axios.post(`${API_BASE_URL}/classify`, {
        text,
        model,
      });
      setResult(response.data);
      return response.data;
    } catch (err) {
      setError(err.response?.data?.error || "Erro ao processar o email");
      throw err;
    } finally {
      setLoading(false);
    }
  };

  // Para arquivo
  const analyzeFile = async (file, model = "gemini") => {
    if (!file) {
      setError("Por favor, selecione um arquivo");
      return;
    }
    setLoading(true);
    setError(null);
    setResult(null);

    const formData = new FormData();
    formData.append("file", file);
    formData.append("model", model);

    try {
      const response = await axios.post(
        `${API_BASE_URL}/classify-file`,
        formData,
        {
          headers: { "Content-Type": "multipart/form-data" },
        }
      );
      setResult(response.data);
      return response.data;
    } catch (err) {
      setError(err.response?.data?.error || "Erro ao processar o arquivo");
      throw err;
    } finally {
      setLoading(false);
    }
  };

  // Limpar estado
  const clear = () => {
    setResult(null);
    setError(null);
    setLoading(false);
  };

  return { loading, result, error, analyzeText, analyzeFile, clear };
}
