import React, { useState } from 'react';
import { useDropzone } from 'react-dropzone';
import { Upload, FileText, Mail, CheckCircle, AlertCircle, Trash2, BrainCircuit, ListTodo, Cpu } from 'lucide-react';
import axios from 'axios';

// Define o endereço base da sua API Flask
const API_BASE_URL = 'http://127.0.0.1:5000';

const GlobalStyles = () => (
  <style>{`
    /* --- GERAL --- */
    @import url('https://fonts.googleapis.com/css2?family=Inter:wght@400;500;600;700&display=swap');
    :root {
      --primary-color: #4f46e5; --primary-hover: #4338ca; --background-color: #f3f4f6;
      --card-background: #ffffff; --text-color: #1f2937; --text-light: #6b7280;
      --border-color: #d1d5db; --error-color: #ef4444; --productive-bg: #dcfce7;
      --productive-text: #166534; --unproductive-bg: #fee2e2; --unproductive-text: #991b1b;
    }
    body { margin: 0; font-family: 'Inter', sans-serif; background-color: var(--background-color); color: var(--text-color); }
    .container { max-width: 700px; margin: 2rem auto; padding: 1rem; }
    .header { text-align: center; margin-bottom: 2rem; }
    .header h1 { font-size: 2.25rem; font-weight: 700; display: flex; align-items: center; justify-content: center; gap: 0.75rem; }
    .header p { font-size: 1.125rem; color: var(--text-light); max-width: 500px; margin: 0.5rem auto 0; }
    .main-card { background-color: var(--card-background); border-radius: 12px; box-shadow: 0 4px 6px -1px rgb(0 0 0 / 0.1), 0 2px 4px -2px rgb(0 0 0 / 0.1); padding: 2rem; }
    .section-title { font-size: 1.5rem; font-weight: 600; margin: 0 0 0.25rem 0; }
    .section-description { color: var(--text-light); margin-top: 0; margin-bottom: 1.5rem; }
    .upload-tabs { display: flex; border-bottom: 1px solid var(--border-color); margin-bottom: 1.5rem; }
    .tab-button { display: flex; align-items: center; gap: 0.5rem; padding: 0.75rem 1.25rem; border: none; background: none; cursor: pointer; font-size: 1rem; font-weight: 500; color: var(--text-light); position: relative; transition: color 0.2s ease; }
    .tab-button:hover { color: var(--text-color); }
    .tab-button.active { color: var(--primary-color); font-weight: 600; }
    .tab-button.active::after { content: ''; position: absolute; bottom: -1px; left: 0; right: 0; height: 2px; background-color: var(--primary-color); }
    .text-input-container { position: relative; }
    .text-input { width: 100%; min-height: 150px; padding: 0.75rem; border: 1px solid var(--border-color); border-radius: 8px; font-size: 1rem; font-family: inherit; resize: vertical; box-sizing: border-box; }
    .text-input:focus { outline: none; border-color: var(--primary-color); box-shadow: 0 0 0 2px rgba(79, 70, 229, 0.2); }
    .clear-icon { position: absolute; top: 10px; right: 10px; color: var(--text-light); cursor: pointer; transition: color 0.2s ease; }
    .clear-icon:hover { color: var(--text-color); }
    .dropzone { border: 2px dashed var(--border-color); border-radius: 8px; padding: 2rem; text-align: center; cursor: pointer; transition: border-color 0.2s ease, background-color 0.2s ease; }
    .dropzone:hover, .dropzone.active { border-color: var(--primary-color); background-color: #f9fafb; }
    .dropzone-content { display: flex; flex-direction: column; align-items: center; gap: 0.5rem; }
    .upload-icon { color: var(--primary-color); }
    .dropzone-text { font-weight: 500; }
    .dropzone-subtext { font-size: 0.875rem; color: var(--text-light); }
    .file-info { margin-top: 1rem; padding: 0.75rem; background-color: #f9fafb; border: 1px solid var(--border-color); border-radius: 8px; display: flex; justify-content: space-between; align-items: center; }
    .clear-file-icon { cursor: pointer; color: var(--text-light); }
    .clear-file-icon:hover { color: var(--error-color); }
    .submit-button { width: 100%; padding: 0.75rem; margin-top: 1.5rem; background-color: var(--primary-color); color: white; border: none; border-radius: 8px; font-size: 1rem; font-weight: 600; cursor: pointer; transition: background-color 0.2s ease; }
    .submit-button:hover:not(:disabled) { background-color: var(--primary-hover); }
    .submit-button:disabled { background-color: #9ca3af; cursor: not-allowed; }
    .loading { display: flex; align-items: center; justify-content: center; gap: 0.5rem; }
    .spinner { width: 18px; height: 18px; border: 2px solid rgba(255, 255, 255, 0.3); border-top-color: white; border-radius: 50%; animation: spin 1s linear infinite; }
    @keyframes spin { to { transform: rotate(360deg); } }
    .error-message { margin-top: 1.5rem; padding: 0.75rem; background-color: var(--unproductive-bg); color: var(--unproductive-text); border: 1px solid var(--error-color); border-radius: 8px; display: flex; align-items: center; gap: 0.5rem; font-weight: 500; }
    .results-section { margin-top: 2rem; border-top: 1px solid var(--border-color); padding-top: 2rem; }
    .result-card { border: 1px solid var(--border-color); border-radius: 8px; padding: 1.5rem; }
    .classification-result { display: flex; align-items: center; justify-content: space-between; margin-bottom: 1.5rem; }
    .category-badge { display: flex; align-items: center; gap: 0.5rem; padding: 0.25rem 0.75rem; border-radius: 9999px; font-weight: 600; }
    .category-productive { background-color: var(--productive-bg); color: var(--productive-text); }
    .category-unproductive { background-color: var(--unproductive-bg); color: var(--unproductive-text); }
    .category-undefined { background-color: #e5e7eb; color: #4b5563; }
    .confidence-score { font-size: 0.875rem; color: var(--text-light); }
    .insight-grid { display: grid; grid-template-columns: 1fr 1fr; gap: 1rem; margin-bottom: 1.5rem; }
    .insight-card { background-color: #f9fafb; border: 1px solid var(--border-color); border-radius: 8px; padding: 1rem; }
    .insight-header { display: flex; align-items: center; gap: 0.5rem; color: var(--text-color); margin-bottom: 0.5rem; }
    .insight-header h3 { margin: 0; font-size: 1rem; font-weight: 600; }
    .insight-card p, .insight-card ul { margin: 0; font-size: 0.875rem; color: var(--text-light); }
    .insight-card ul { padding-left: 1.25rem; }
    .response-section { margin-top: 1.5rem; }
    .response-title { font-weight: 600; margin-bottom: 0.5rem; }
    .suggested-response { background-color: #f9fafb; border: 1px solid var(--border-color); border-radius: 8px; padding: 1rem; font-style: italic; color: var(--text-light); }
    .analyzed-by { margin-top: 1.5rem; text-align: center; font-size: 0.8rem; color: var(--text-light); display: flex; align-items: center; justify-content: center; gap: 0.3rem; }
    /* --- SELETOR DE MODELO --- */
    .model-selector { margin-top: 1.5rem; padding-top: 1.5rem; border-top: 1px solid var(--border-color); }
    .model-selector-title { display: block; font-weight: 500; color: var(--text-color); margin-bottom: 0.75rem; text-align: center; }
    .model-options { display: flex; justify-content: center; gap: 1rem; }
    .model-option { flex: 1; display: flex; align-items: center; justify-content: center; gap: 0.5rem; padding: 0.75rem; border: 1px solid var(--border-color); background-color: #f9fafb; color: var(--text-light); border-radius: 8px; cursor: pointer; font-size: 0.875rem; font-weight: 500; transition: all 0.2s ease; }
    .model-option:hover { background-color: #f3f4f6; border-color: #9ca3af; }
    .model-option.active { background-color: var(--primary-color); border-color: var(--primary-color); color: white; box-shadow: 0 4px 6px -1px rgb(0 0 0 / 0.1); }
  `}</style>
);

function App() {
  const [activeTab, setActiveTab] = useState('text');
  const [emailText, setEmailText] = useState('');
  const [loading, setLoading] = useState(false);
  const [result, setResult] = useState(null);
  const [error, setError] = useState(null);
  const [uploadedFile, setUploadedFile] = useState(null);
  const [analysisModel, setAnalysisModel] = useState('gemini'); // 'gemini' or 'legacy'

  const clearState = () => { setError(null); setResult(null); }

  const onDrop = (acceptedFiles) => {
    const file = acceptedFiles[0];
    if (file) { setUploadedFile(file); clearState(); }
  };

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop, accept: { 'text/plain': ['.txt'], 'application/pdf': ['.pdf'] }, maxFiles: 1
  });

  const handleTextSubmit = async () => {
    if (!emailText.trim()) { setError('Por favor, insira o texto do email'); return; }
    setLoading(true); clearState();
    try {
      const response = await axios.post(`${API_BASE_URL}/classify`, { text: emailText, model: analysisModel });
      setResult(response.data);
    } catch (err) { setError(err.response?.data?.error || 'Erro ao processar o email'); }
    finally { setLoading(false); }
  };

  const handleFileSubmit = async () => {
    if (!uploadedFile) { setError('Por favor, selecione um arquivo'); return; }
    setLoading(true); clearState();
    const formData = new FormData();
    formData.append('file', uploadedFile);
    formData.append('model', analysisModel);
    try {
      const response = await axios.post(`${API_BASE_URL}/classify-file`, formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      setResult(response.data);
    } catch (err) { setError(err.response?.data?.error || 'Erro ao processar o arquivo'); }
    finally { setLoading(false); }
  };

  const handleSubmit = () => {
    if (activeTab === 'text') handleTextSubmit();
    else handleFileSubmit();
  };

  const handleClearText = () => { setEmailText(''); clearState(); }

  return (
    <>
      <GlobalStyles />
      <div className="container">
        <div className="header">
          <h1><Mail className="inline-icon" /> Analisador de E-mails com IA</h1>
          <p>Use o poder do Gemini para classificar e-mails, gerar respostas e extrair insights.</p>
        </div>
        <div className="main-card">
          <div className="upload-section">
            <h2 className="section-title">Insira um e-mail para análise</h2>
            <p className='section-description'>Pode colar o texto ou fazer o upload de um ficheiro.</p>
            <div className="upload-tabs">
              <button className={`tab-button ${activeTab === 'text' ? 'active' : ''}`} onClick={() => { setActiveTab('text'); clearState(); setUploadedFile(null); }}>
                <FileText size={18} /> Texto
              </button>
              <button className={`tab-button ${activeTab === 'file' ? 'active' : ''}`} onClick={() => { setActiveTab('file'); clearState(); setEmailText(''); }}>
                <Upload size={18} /> Upload de ficheiro
              </button>
            </div>
            {activeTab === 'text' ? (
              <div className="text-input-container">
                <textarea className="text-input" placeholder="Cole aqui o conteúdo do email que deseja classificar..." value={emailText} onChange={(e) => setEmailText(e.target.value)} />
                {emailText && <Trash2 size={18} className='clear-icon' title="Limpar texto" onClick={handleClearText} />}
              </div>
            ) : (
              <div>
                <div {...getRootProps()} className={`dropzone ${isDragActive ? 'active' : ''}`}>
                  <input {...getInputProps()} />
                  <div className="dropzone-content">
                    <Upload size={48} className="upload-icon" />
                    <div className="dropzone-text">{isDragActive ? 'Solte o ficheiro aqui' : 'Clique ou arraste um ficheiro'}</div>
                    <div className="dropzone-subtext">Formatos suportados: .txt, .pdf</div>
                  </div>
                </div>
                {uploadedFile && (
                  <div className="file-info">
                    <strong>Ficheiro selecionado:</strong> {uploadedFile.name} ({(uploadedFile.size / 1024).toFixed(1)} KB)
                    <Trash2 size={16} className="clear-file-icon" onClick={() => { setUploadedFile(null); clearState(); }} />
                  </div>
                )}
              </div>
            )}
            <div className="model-selector">
              <label className="model-selector-title">Escolha o motor de análise:</label>
              <div className="model-options">
                <button className={`model-option ${analysisModel === 'legacy' ? 'active' : ''}`} onClick={() => setAnalysisModel('legacy')}>
                  <Cpu size={20} /><span>Básico (Rápido)</span>
                </button>
                <button className={`model-option ${analysisModel === 'gemini' ? 'active' : ''}`} onClick={() => setAnalysisModel('gemini')}>
                  <BrainCircuit size={20} /><span>Avançado (Gemini)</span>
                </button>
              </div>
            </div>
            <button className="submit-button" onClick={handleSubmit} disabled={loading || (activeTab === 'text' && !emailText.trim()) || (activeTab === 'file' && !uploadedFile)}>
              {loading ? <div className="loading"><div className="spinner"></div>Analisando...</div> : 'Analisar e-mail'}
            </button>
          </div>
          {error && <div className="error-message"><AlertCircle size={20} />{error}</div>}
          {result && (
            <div className="results-section">
              <h2 className="section-title">Resultados da Análise</h2>
              <div className="result-card">
                <div className="classification-result">
                  <span className={`category-badge ${result.category === 'Produtivo' ? 'category-productive' : result.category === 'Improdutivo' ? 'category-unproductive' : 'category-undefined'}`}>
                    {result.category === 'Produtivo' ? <CheckCircle size={16} /> : <AlertCircle size={16} />}
                    {result.category}
                  </span>
                  <span className="confidence-score">Confiança: <strong>{(result.confidence * 100).toFixed(0)}%</strong></span>
                </div>
                <div className="insight-grid">
                  <div className="insight-card">
                    <div className="insight-header"><BookText size={18} /><h3>Resumo</h3></div>
                    <p>{result.summary}</p>
                  </div>
                  <div className="insight-card">
                    <div className="insight-header"><ListTodo size={18} /><h3>Itens de Ação</h3></div>
                    {result.action_items && result.action_items.length > 0 ? (
                      <ul>{result.action_items.map((item, index) => <li key={index}>{item}</li>)}</ul>
                    ) : (<p>Nenhum item de ação claro foi identificado.</p>)}
                  </div>
                </div>
                <div className="response-section">
                  <div className="response-title">Resposta Sugerida:</div>
                  <div className="suggested-response">"{result.suggested_response}"</div>
                </div>
                {result.analyzed_by && (
                  <div className="analyzed-by">
                    {result.analyzed_by.includes('gemini') ? <BrainCircuit size={14} /> : <Cpu size={14} />}
                    Analisado por: {result.analyzed_by.includes('gemini') ? result.analyzed_by.replace('models/', '') : 'Modelo Básico'}
                  </div>
                )}
              </div>
            </div>
          )}
        </div>
      </div>
    </>
  );
}

export default App;
