import React from "react";
import { useDropzone } from "react-dropzone";
import {
  Upload,
  FileText,
  CheckCircle,
  AlertCircle,
  Trash2,
  BrainCircuit,
  ListTodo,
  Cpu,
} from "lucide-react";
import axios from "axios";
import "./App.css";
import { Header } from "./components/layouts/Header";

const API_BASE_URL = "http://127.0.0.1:5000";

function App() {
  const [activeTab, setActiveTab] = React.useState("text");
  const [emailText, setEmailText] = React.useState("");
  const [loading, setLoading] = React.useState(false);
  const [result, setResult] = React.useState(null);
  const [error, setError] = React.useState(null);
  const [uploadedFile, setUploadedFile] = React.useState(null);
  const [analysisModel, setAnalysisModel] = React.useState("gemini"); // 'gemini' or 'legacy'

  const clearState = () => {
    setError(null);
    setResult(null);
  };

  const onDrop = (acceptedFiles) => {
    const file = acceptedFiles[0];
    if (file) {
      setUploadedFile(file);
      clearState();
    }
  };

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: { "text/plain": [".txt"], "application/pdf": [".pdf"] },
    maxFiles: 1,
    onDropRejected: (rejections) => {
      toast.error('Formato de arquivo não suportado. Use apenas .txt ou .pdf')
    }
  });

  const handleTextSubmit = async () => {
    if (!emailText.trim()) {
      setError("Por favor, insira o texto do email");
      return;
    }
    setLoading(true);
    clearState();
    try {
      const response = await axios.post(`${API_BASE_URL}/classify`, {
        text: emailText,
        model: analysisModel,
      });
      setResult(response.data);
    } catch (err) {
      setError(err.response?.data?.error || "Erro ao processar o email");
    } finally {
      setLoading(false);
    }
  };

  const handleFileSubmit = async () => {
    if (!uploadedFile) {
      setError("Por favor, selecione um arquivo");
      return;
    }
    setLoading(true);
    clearState();
    const formData = new FormData();
    formData.append("file", uploadedFile);
    formData.append("model", analysisModel);
    try {
      const response = await axios.post(
        `${API_BASE_URL}/classify-file`,
        formData,
        {
          headers: { "Content-Type": "multipart/form-data" },
        }
      );
      setResult(response.data);
    } catch (err) {
      setError(err.response?.data?.error || "Erro ao processar o arquivo");
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = () => {
    if (activeTab === "text") handleTextSubmit();
    else handleFileSubmit();
  };

  const handleClearText = () => {
    setEmailText("");
    clearState();
  };

  return (
    <>
      <Header />
      <div className="container">
        <div className="main-card">
          <div className="upload-section">
            <h2 className="section-title">Insira um e-mail para análise</h2>
            <p className="section-description">
              Pode colar um texto ou fazer o upload de um ficheiro.
            </p>
            <div className="upload-tabs">
              <button
                className={`tab-button ${activeTab === "text" ? "active" : ""}`}
                onClick={() => {
                  setActiveTab("text");
                  clearState();
                  setUploadedFile(null);
                }}
              >
                <FileText size={18} /> Texto
              </button>
              <button
                className={`tab-button ${activeTab === "file" ? "active" : ""}`}
                onClick={() => {
                  setActiveTab("file");
                  clearState();
                  setEmailText("");
                }}
              >
                <Upload size={18} /> Upload de ficheiro
              </button>
            </div>
            {activeTab === "text" ? (
              <div className="text-input-container">
                <textarea
                  className="text-input"
                  placeholder="Cole aqui o conteúdo do email que deseja classificar..."
                  value={emailText}
                  onChange={(e) => setEmailText(e.target.value)}
                />
                {emailText && (
                  <Trash2
                    size={18}
                    className="clear-icon"
                    title="Limpar texto"
                    onClick={handleClearText}
                  />
                )}
              </div>
            ) : (
              <div>
                <div
                  {...getRootProps()}
                  className={`dropzone ${isDragActive ? "active" : ""}`}
                >
                  <input {...getInputProps()} />
                  <div className="dropzone-content">
                    <Upload size={48} className="upload-icon" />
                    <div className="dropzone-text">
                      {isDragActive
                        ? "Solte o ficheiro aqui"
                        : "Clique ou arraste um ficheiro"}
                    </div>
                    <div className="dropzone-subtext">
                      Formatos suportados: .txt, .pdf
                    </div>
                  </div>
                </div>
                {uploadedFile && (
                  <div className="file-info">
                    <strong>Ficheiro selecionado:</strong> {uploadedFile.name} (
                    {(uploadedFile.size / 1024).toFixed(1)} KB)
                    <Trash2
                      size={16}
                      className="clear-file-icon"
                      onClick={() => {
                        setUploadedFile(null);
                        clearState();
                      }}
                    />
                  </div>
                )}
              </div>
            )}
            <div className="model-selector">
              <label className="model-selector-title">
                Escolha o motor de análise:
              </label>
              <div className="model-options">
                <button
                  className={`model-option ${
                    analysisModel === "legacy" ? "active" : ""
                  }`}
                  onClick={() => setAnalysisModel("legacy")}
                >
                  <Cpu size={20} />
                  <span>Básico (Rápido)</span>
                </button>
                <button
                  className={`model-option ${
                    analysisModel === "gemini" ? "active" : ""
                  }`}
                  onClick={() => setAnalysisModel("gemini")}
                >
                  <BrainCircuit size={20} />
                  <span>Avançado (Gemini)</span>
                </button>
              </div>
            </div>
            <button
              className="submit-button"
              onClick={handleSubmit}
              disabled={
                loading ||
                (activeTab === "text" && !emailText.trim()) ||
                (activeTab === "file" && !uploadedFile)
              }
            >
              {loading ? (
                <div className="loading">
                  <div className="spinner"></div>Analisando...
                </div>
              ) : (
                "Analisar e-mail"
              )}
            </button>
          </div>
          {error && (
            <div className="error-message">
              <AlertCircle size={20} />
              {error}
            </div>
          )}
          {result && (
            <div className="results-section">
              <h2 className="section-title">Resultados da Análise</h2>
              <div className="result-card">
                <div className="classification-result">
                  <span
                    className={`category-badge ${
                      result.category === "Produtivo"
                        ? "category-productive"
                        : result.category === "Improdutivo"
                        ? "category-unproductive"
                        : "category-undefined"
                    }`}
                  >
                    {result.category === "Produtivo" ? (
                      <CheckCircle size={16} />
                    ) : (
                      <AlertCircle size={16} />
                    )}
                    {result.category}
                  </span>
                  <span className="confidence-score">
                    Confiança:{" "}
                    <strong>{(result.confidence * 100).toFixed(0)}%</strong>
                  </span>
                </div>
                <div className="insight-grid">
                  <div className="insight-card">
                    <div className="insight-header">
                      <h3>Resumo</h3>
                    </div>
                    <p>{result.summary}</p>
                  </div>
                  <div className="insight-card">
                    <div className="insight-header">
                      <ListTodo size={18} />
                      <h3>Itens de Ação</h3>
                    </div>
                    {result.action_items && result.action_items.length > 0 ? (
                      <ul>
                        {result.action_items.map((item, index) => (
                          <li key={index}>{item}</li>
                        ))}
                      </ul>
                    ) : (
                      <p>Nenhum item de ação claro foi identificado.</p>
                    )}
                  </div>
                </div>
                <div className="response-section">
                  <div className="response-title">Resposta Sugerida:</div>
                  <div className="suggested-response">
                    "{result.suggested_response}"
                  </div>
                </div>
                {result.analyzed_by && (
                  <div className="analyzed-by">
                    {result.analyzed_by.includes("gemini") ? (
                      <BrainCircuit size={14} />
                    ) : (
                      <Cpu size={14} />
                    )}
                    Analisado por:{" "}
                    {result.analyzed_by.includes("gemini")
                      ? result.analyzed_by.replace("models/", "")
                      : "Modelo Básico"}
                  </div>
                )}
              </div>
            </div>
          )}
        </div>
      </div>
    </>
  );
}

export default App;
