import React, { useState } from 'react';
import { useDropzone } from 'react-dropzone';
import { Upload, FileText, Mail, CheckCircle, AlertCircle } from 'lucide-react';
import axios from 'axios';
import './App.css';

function App() {
  const [activeTab, setActiveTab] = useState('text');
  const [emailText, setEmailText] = useState('');
  const [loading, setLoading] = useState(false);
  const [result, setResult] = useState(null);
  const [error, setError] = useState(null);
  const [uploadedFile, setUploadedFile] = useState(null);

  const onDrop = (acceptedFiles) => {
    const file = acceptedFiles[0];
    if (file) {
      setUploadedFile(file);
      setError(null);
    }
  };

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: {
      'text/plain': ['.txt'],
      'application/pdf': ['.pdf']
    },
    maxFiles: 1
  });

  const handleTextSubmit = async () => {
    if (!emailText.trim()) {
      setError('Por favor, insira o texto do email');
      return;
    }

    setLoading(true);
    setError(null);
    setResult(null);

    try {
      const response = await axios.post('/classify', {
        text: emailText
      });
      setResult(response.data);
    } catch (err) {
      setError(err.response?.data?.error || 'Erro ao processar o email');
    } finally {
      setLoading(false);
    }
  };

  const handleFileSubmit = async () => {
    if (!uploadedFile) {
      setError('Por favor, selecione um arquivo');
      return;
    }

    setLoading(true);
    setError(null);
    setResult(null);

    const formData = new FormData();
    formData.append('file', uploadedFile);

    try {
      const response = await axios.post('/classify-file', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      setResult(response.data);
    } catch (err) {
      setError(err.response?.data?.error || 'Erro ao processar o arquivo');
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = () => {
    if (activeTab === 'text') {
      handleTextSubmit();
    } else {
      handleFileSubmit();
    }
  };

  return (
    <div className="container">
      <div className="header">
        <h1>
          <Mail className="inline-icon" />
          Classificador de e-mails IA
        </h1>
        <p>Sistema inteligente para classificação automática de emails e sugestão de respostas</p>
      </div>

      <div className="main-card">
        <div className="upload-section">
          <h2 className="section-title">Inserir Email para Análise</h2>

          <div className="upload-tabs">
            <button
              className={`tab-button ${activeTab === 'text' ? 'active' : ''}`}
              onClick={() => setActiveTab('text')}
            >
              <FileText size={18} />
              Texto direto
            </button>
            <button
              className={`tab-button ${activeTab === 'file' ? 'active' : ''}`}
              onClick={() => setActiveTab('file')}
            >
              <Upload size={18} />
              Upload de arquivo
            </button>
          </div>

          {activeTab === 'text' ? (
            <div className="text-input-container">
              <textarea
                className="text-input"
                placeholder="Cole aqui o conteúdo do email que deseja classificar..."
                value={emailText}
                onChange={(e) => setEmailText(e.target.value)}
              />
            </div>
          ) : (
            <div>
              <div {...getRootProps()} className={`dropzone ${isDragActive ? 'active' : ''}`}>
                <input {...getInputProps()} />
                <div className="dropzone-content">
                  <Upload size={48} className="upload-icon" />
                  <div className="dropzone-text">
                    {isDragActive ? 'Solte o arquivo aqui' : 'Clique ou arraste um arquivo'}
                  </div>
                  <div className="dropzone-subtext">
                    Formatos suportados: .txt, .pdf (máx. 10MB)
                  </div>
                </div>
              </div>

              {uploadedFile && (
                <div className="file-info">
                  <strong>Arquivo selecionado:</strong> {uploadedFile.name}
                  ({(uploadedFile.size / 1024).toFixed(1)} KB)
                </div>
              )}
            </div>
          )}

          <button
            className="submit-button"
            onClick={handleSubmit}
            disabled={loading || (activeTab === 'text' && !emailText.trim()) || (activeTab === 'file' && !uploadedFile)}
          >
            {loading ? (
              <div className="loading">
                <div className="spinner"></div>
                Processando...
              </div>
            ) : (
              'Classificar Email'
            )}
          </button>
        </div>

        {error && (
          <div className="error-message">
            <AlertCircle size={20} style={{ marginRight: '8px', verticalAlign: 'middle' }} />
            {error}
          </div>
        )}

        {result && (
          <div className="results-section">
            <h2 className="section-title">Resultados da Análise</h2>

            <div className="result-card">
              <div className="classification-result">
                <span className={`category-badge ${result.category === 'Produtivo' ? 'category-productive' : 'category-unproductive'}`}>
                  {result.category === 'Produtivo' ? (
                    <CheckCircle size={16} style={{ marginRight: '5px', verticalAlign: 'middle' }} />
                  ) : (
                    <AlertCircle size={16} style={{ marginRight: '5px', verticalAlign: 'middle' }} />
                  )}
                  {result.category}
                </span>
                <span className="confidence-score">
                  Confiança: {(result.confidence * 100).toFixed(0)}%
                </span>
              </div>

              <div className="response-section">
                <div className="response-title">Resposta Sugerida:</div>
                <div className="suggested-response">
                  "{result.suggested_response}"
                </div>
              </div>

              {result.email_preview && (
                <div className="email-preview">
                  <div className="preview-title">Prévia do Email:</div>
                  <div className="preview-text">{result.email_preview}</div>
                </div>
              )}

              {result.filename && (
                <div className="file-info" style={{ marginTop: '15px' }}>
                  <strong>Arquivo processado:</strong> {result.filename}
                </div>
              )}
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
